    },
    mounted() {
        this.read(),
        this.showImage()
        this.addEmoji()
    },
    methods:{
        leftmove:function(){
            if(this.page===0){
                return
            }else if(this.page ===1){
                let width = $('.items').width()
                $('#box').css({
                    transform: `translateX(0px)`
                })
                this.page = this.page - 1
            }else{
                let width = $('.items').width()
                let number = -width * (this.page - 1)
                $('#box').css({
                    transform: `translateX(${number}px)`
                })
                this.page = this.page - 1
            }
        },
        rightmove:function(){
            let width = $('.items').width()
            let page = this.page + 1
            let number = -width * page
            $('#box').css({
                transform: `translateX(${number}px)`
            })
            this.page = this.page + 1
        },
        save:function(){
            var ManData = AV.Object.extend('ManData');
            var username = this.user.username
            var userImage = this.user.userImage
            var userSign = this.user.userSign
            var content = emojione.toImage(this.textarea)
            var good = 0;
            var goodUser = [];
            var comment = [];
            var commentNumber = 0
            var imageUrl = []


            $('.image-item').children().each(function () {
                let src = this.src
                imageUrl.push(src)
            })
            var ManData = new ManData();
            ManData.set('username', username);
            ManData.set('usersign', userSign);
            ManData.set('userImage', userImage);
            ManData.set('content', content);
            ManData.set('good', good);
            ManData.set('goodUser', goodUser);
            ManData.set('commentNumber', commentNumber);
            ManData.set('comment', comment);
            ManData.set('imageUrl', imageUrl);
            ManData.save().then( (ManData)=> {
                this.editorshow = false
                $('#imageBox').empty()
                this.textarea = ''
                this.read()
            }, function(error) {
                // Â§±Ë¥•
            });
        },
        read:function(){
            this.ManData = []
            var query = new AV.Query('ManData');
            query.find().then((ManData)=> {  
                ManData.reverse()
                for(let i=0;i<ManData.length;i++){ 
                    this.ManData.push({
                        username : ManData[i].attributes.username,
                        content : ManData[i].attributes.content,
                        good : ManData[i].attributes.good,
                        comment : ManData[i].attributes.comment,
                        commentNumber : ManData[i].attributes.commentNumber,
                        imageUrl : ManData[i].attributes.imageUrl,
                        usersign : ManData[i].attributes.usersign,
                        userImage : ManData[i].attributes.userImage,
                        id : ManData[i].id,
                        goodUser :ManData[i].attributes.goodUser,
                        date :ManData[i].attributes.date,
                    })
                    for(let ii = 0;ii < this.ManData[i].goodUser.length;ii++){
                        if(this.ManData[i].goodUser[ii] === this.user.username){
                            this.ManData[i].good = 1
                            break   
                        }else{
                            this.ManData[i].good = 0
                        } 
                    }

                }
            }, function(error){
                console.error(error) 
            })
        },
        getIndex:function(index){
            $('.Lost>#window').css('display','flex')
            this.item = this.ManData[index]
            this.item.commentNumber = this.ManData[index].comment.length
            this.item.goodNumber = this.ManData[index].goodUser.length
        },
        saveImage:function(){
            console.log('sdd')
            let imageBox = $('#SelectionImage')
            let imageNumber = imageBox.children().length
            if(imageNumber>=9){
                return
            }else{
                var fileUploadControl = $('#SelectionPhoto')[0];
                if (fileUploadControl.files.length > 0) {
                    var localFile = fileUploadControl.files[0];
                    var name = 'SelectionImage.jpg';

                    var file = new AV.File(name, localFile);
                    file.save().then((file)=> {
                        let url = file.url()
                        let image = $(`<div class="image-item">
                                        <img src="${url}" alt="">
                                        </div>`)
                        
                        image.appendTo("#SelectionImage")
                        setTimeout(function() {
                            let width = image.children().width()
                            let height = image.children().height()
                            if (width<height) {
                                image.children().css('width','185px')
                            }else{
                                image.children().css('height','185px')
                            }
                        }, 1000);
                    }, function(error) {
                        // ÂºÇÂ∏∏Â§ÑÁêÜ
                        console.error(error);
                    });
                }
            }
        },
        showImage(){
            $('#SelectionPhoto').change(()=> {
                this.saveImage()
            }); 
        },
        userCard:function(username){
            $('.Lost>#userCard').css('display','flex')
            var query = new AV.Query('_User');
            query.find().then((_User)=> {  
                for(let i = 0;i<_User.length;i++){
                    if(_User[i].attributes.username === username){
                        this.card.username = _User[i].attributes.username
                        this.card.gender = _User[i].attributes.gender
                        this.card.userSign = _User[i].attributes.userSign
                        this.card.realName = _User[i].attributes.realName
                        this.card.userImage = _User[i].attributes.userImage
                        this.card.major = _User[i].attributes.major
                        this.card.callWhat = _User[i].attributes.callWhat
                        this.card.call = _User[i].attributes.call
                    }
                }
            }, function(error){
                console.error(error) 
            })
        },
        good:function(index){
            for(let i = 0;i<=this.ManData[index].goodUser.length;i++){
                if(this.ManData[index].goodUser[i] === this.user.username){
                    return
                }          
            }
            this.ManData[index].good = 1
            this.ManData[index].goodUser.push(this.user.username)
            var ManData = AV.Object.createWithoutData('ManData', this.ManData[index].id);
            // ‰øÆÊîπÂ±ûÊÄß
            ManData.set('good', this.ManData[index].good);
            ManData.set('goodUser', this.ManData[index].goodUser);
            // ‰øùÂ≠òÂà∞‰∫ëÁ´Ø
            ManData.save();
            
        },
        addEmoji:function(){
            $('.emoji').children().click((emoji)=> {
                this.textarea = this.textarea + emoji.target.alt
                this.emojishow = false
            })
        },

    }
}
</script>
